// Lightbox Script

const lightbox = document.querySelector('.lightbox-button');
const lightboxContent = document.createElement('div');

lightbox.addEventListener('click', () => {
    lightboxContent.innerHTML = `<h2>${lightbox.dataset.title}</h2><p>${lightbox.dataset.description}</p>`;
    lightboxContent.style.display = 'block';
});

document.body.appendChild(lightboxContent);

// Close Lightbox on click outside
document.addEventListener('click', (event) => {
    if (!lightbox.contains(event.target)) {
        lightboxContent.style.display = 'none';
    }
});
